import java.util.Scanner;

    public class test1 {

        private static void bubble(int[] arr, int length){
            int i, j, t; 

            for (i = length -2; i >=0; i--){
                for (j=0; j<=i; j++){
                    if (arr[j] > arr[j+1]){
                        t = arr[j]; 
                        arr[j] = arr[j+1]; 
                        arr[j+1] = t; 
                    }
                }
            }
        }

        public static void main(String[] arrs) {
            int[] arr = new int[100]; 
            int n, i;

            System.out.printf("\n\n 输入要排序的整数元素数量：");

            Scanner scanner = new Scanner(System.in);
            n = scanner.nextInt(); 
            scanner.close(); 

            for (i = 0; i < n; i++){
                System.out.printf("\n\n 为第 %d 个元素输入整数值：", i+1);
                arr[i] = scanner.nextInt(); 
            }

            bubble(arr,n); 

            System.out.printf("\n\n 最终排序后的数组为："); 
            for (i = 0; i<= n-1; i++){
                System.out.printf("%d ", arr[i]);
            }
        }
    }

